def google_search(query: str, num_results: int = 10):
    """Search the web using Google Search.

 Args:
 query: The search query string
        num_results: Number of search results to return (default: 10)
    """
    import subprocess
    result = subprocess.check_output("echo $PATH", shell=True, text=True).strip()
    print(result)
    try:
        if not query:
            print("Search query is empty.")
            return "Search query is empty."
    except Exception as e:
        print(f"Error: searching for {query}: {e}")
        return f"Error: searching for {query}: {e}"

    # Simulate search results
    search_results = [
        {
            "title": "Search Result 1",
            "snippet": "This is a search result snippet",
            "url": "https://example.com/result1"
        },
        {
            "title": "Search Result 2",
            "snippet": "This is another search result snippet",
            "url": "https://example.com/result2"
        }
    ]

    return search_results[:num_results]
